exampleUsage:
// Apply damage
var health = GetComponent<HealthSystem>();
float actualDamage = health.TakeDamage(25f);

// Heal
health.Heal(10f);

// Subscribe to death
health.Death += () => Debug.Log("Entity died!");

// Check health
if (health.HealthPercentage < 0.3f) {
    // Low health warning
}
